{
  "patient_id": "JD12345",
  "demographics": {"name": "John Doe", "age": 68, "sex": "male"},
  "diagnoses": [{"code": "I48.91", "description": "Atrial fibrillation, unspecified"}, {"code": "N18.3", "description": "Chronic kidney disease, stage 3"}, {"code": "I10", "description": "Essential hypertension"}, {"code": "E11.9", "description": "Type 2 diabetes mellitus without complications"}],
  "labs": {
    "creatinine": 1.5,
    "eGFR": 45,
    "INR": 2.6,
    "HbA1c": 7.2
  },
  "medications": [{"name": "warfarin", "dose": "5 mg", "frequency": "daily"}],
  "allergies": []
}

{
  "medications_checked": ["warfarin", "apixaban"],
  "interactions": [
    {
      "type": "pharmacodynamic",
      "severity": "moderate",
      "description": "Concurrent use increases risk of major bleeding. A washout period of 2‑3 days after stopping warfarin (once INR < 2.0) is recommended before initiating apixaban."
    }
  ]
}

{
  "guideline": "ACC/AHA 2023 Guideline for Management of Atrial Fibrillation",
  "recommendations": {
    "anticoagulation": {
      "preferred_agent": "DOAC",
      "agent_options": ["apixaban", "dabigatran", "rivaroxaban", "edoxaban"],
      "dose_adjustment": {
        "apixaban": {
          "standard": "5 mg BID",
          "reduced": "2.5 mg BID",
          "criteria_for_reduction": ["age ≥ 80", "weight ≤ 60 kg", "serum creatinine ≥ 1.5 mg/dL"]
        }
      }
    }
  }
}
