{
  "guideline": "ACC/AHA 2022 Peri‑operative Anemia Management",
  "recommendations": [
    {"step": 1, "action": "Confirm iron‑deficiency etiology with ferritin <30 ng/mL or TSAT <20%"},
    {"step": 2, "action": "Oral iron 325 mg elemental iron daily for 4–6 weeks if surgery >2 weeks away"},
    {"step": 3, "action": "If surgery <2 weeks or oral iron not tolerated, consider IV iron (e.g., iron sucrose 200 mg weekly)"},
    {"step": 4, "action": "Re‑check hemoglobin 1 week after iron therapy; target Hb ≥12 g/dL before surgery"},
    {"step": 5, "action": "Consider erythropoiesis‑stimulating agent (EPO) only if Hb remains <11 g/dL after iron repletion and surgery is urgent"}
  ]
}

{
  "interactions": [],
  "alerts": [
    {"med": "ferrous sulfate", "note": "May reduce absorption of lisinopril; advise dosing 2 h apart"},
    {"med": "iron sucrose", "note": "No known interaction with current meds"},
    {"med": "epoetin alfa", "note": "Monitor for hypertension; patient’s BP is controlled"}
  ]
}

{
  "appointment_date": "2024-11-12",
  "specialty": "hematology",
  "notes": "Initial anemia optimization visit – labs and oral iron counseling"
}
