{
  "guidelines": [
    {
      "source": "ACC/AHA Sports Cardiovascular Screening Guideline",
      "recommendation": "Perform a 12‑lead ECG for all competitive athletes aged 12‑35.",
      "level": "Class I"
    },
    {
      "source": "ACC/AHA Sports Cardiovascular Screening Guideline",
      "recommendation": "If murmur is present, obtain a transthoracic echocardiogram (TTE).",
      "level": "Class I"
    },
    {
      "source": "ACC/AHA Sports Cardiovascular Screening Guideline",
      "recommendation": "Consider a graded exercise stress test if structural heart disease is suspected.",
      "level": "Class IIa"
    }
  ]
}

{
  "suggested_date": "2025-01-22",
  "specialty": "cardiology",
  "notes": "Follow‑up after ECG and TTE results are available."
}
