messages.content:
[
  {
    "document_id": "POL-2023-045",
    "title": "Data Processing Policy – Feature X",
    "excerpt": "All user data collected by Feature X must be processed on the basis of explicit consent as defined in GDPR Art. 6(1)(a). Consent records are stored in the Consent Management System for a minimum of 5 years."
  },
  {
    "document_id": "POL-2022-011",
    "title": "User Consent Guidelines",
    "excerpt": "Explicit consent must be obtained before any personal data is captured. The consent UI must present clear purpose and allow opt‑out."
  }
]

[
  {
    "violation_id": "VIO-2025-001",
    "description": "Feature X does not currently capture explicit user consent before processing personal data, which violates GDPR Art. 6(1)(a).",
    "severity": "high"
  }
]
