messages.content:
{
  "document_id": "POL-2024-09",
  "title": "Biometric Data Handling Policy",
  "excerpt": "Our organization collects facial recognition data solely for secure physical access to data‑center facilities. Templates are stored encrypted using AES‑256 and are retained for a maximum of 12 months after the employee's termination. Access to biometric data is restricted to the Security Operations Team. Consent is obtained via the employee onboarding portal. A DPIA was completed in Q2 2024."
}

[
  {
    "violation_id": "V-001",
    "description": "Retention period for biometric templates (12 months) is shorter than CNIL minimum requirement (2 years) without documented justification.",
    "severity": "medium"
  },
  {
    "violation_id": "V-002",
    "description": "Consent process relies on a generic onboarding portal; CNIL requires explicit, separate consent for biometric data collection.",
    "severity": "high"
  }
]
