[
  {
    "grant_id": "NIH-2022-CRISPR-001",
    "title": "CRISPR‑mediated Reprogramming of Tumor‑Associated Macrophages for Enhanced Immunotherapy",
    "funding_amount_usd": 850000,
    "year": 2022,
    "abstract_snippet": "We demonstrate that CRISPR editing of macrophage checkpoint genes improves antigen presentation and synergizes with PD‑1 blockade in murine melanoma models."
  },
  {
    "grant_id": "NSF-2021-CRISPR-078",
    "title": "Engineering CRISPR‑Based Synthetic Circuits to Modulate Tumor Immune Evasion",
    "funding_amount_usd": 720000,
    "year": 2021,
    "abstract_snippet": "Synthetic CRISPR circuits were designed to sense tumor hypoxia and trigger expression of cytokines that attract cytotoxic T cells, reducing tumor growth in vivo."
  },
  {
    "grant_id": "EU-HEALTH-2020-CRISPR-12",
    "title": "Targeted CRISPR Activation of Neoantigen Genes to Boost Cancer Vaccine Efficacy",
    "funding_amount_usd": 950000,
    "year": 2020,
    "abstract_snippet": "CRISPR activation (CRISPRa) of tumor‑specific neoantigen loci enhances vaccine‑induced T‑cell responses, leading to prolonged survival in mouse models of lung cancer."
  }
]
