messages.content:
[
  {
    "grant_id": "R01CA123456",
    "title": "Targeted CAR‑T cells for solid tumors",
    "reviewer_comments": [
      "The significance statement does not clearly articulate the translational impact on patient survival.",
      "Experimental design lacks power calculations for the in‑vivo mouse studies.",
      "The budget justification for the proposed core facility usage is insufficient."
    ]
  },
  {
    "grant_id": "R01CA234567",
    "title": "Checkpoint blockade synergy with oncolytic viruses",
    "reviewer_comments": [
      "Innovation is modest; similar approaches have been reported recently.",
      "Preliminary data are weak; more pilot experiments are needed before moving to full aims.",
      "The timeline is overly optimistic given the number of animal cohorts planned."
    ]
  },
  {
    "grant_id": "R01CA345678",
    "title": "Neoantigen‑specific TCR engineering for melanoma",
    "reviewer_comments": [
      "The proposal does not address potential off‑target toxicity adequately.",
      "Statistical analysis plan is missing for the clinical correlative studies.",
      "Budget allocation for personnel is unrealistic compared to the scope of work."
    ]
  }
]
