messages.content:
[
  {"type": "voting_rights", "text": "Each Shareholder shall have one vote per share held. Decisions on major matters require a supermajority of 75% of the voting shares."},
  {"type": "tag_along", "text": "If any Shareholder proposes to sell more than 25% of its shares, the other Shareholders have the right to join the sale on the same terms."},
  {"type": "drag_along", "text": "If Shareholders holding at least 70% of the shares approve a sale of the Company, the remaining Shareholders must sell their shares on the same terms."},
  {"type": "forced_buy_out", "text": "The Company may force the purchase of any Shareholder's shares if that Shareholder breaches any material obligation, at a price determined by a mutually agreed independent valuator."}
]

[
  {
    "clause_type": "forced_buy_out",
    "risk": "Potential coercive buy‑out",
    "explanation": "The company can force a purchase of a shareholder's shares on a price set by an independent valuator, which may be unfavorable to the shareholder if no caps or board approval are required.",
    "mitigation": "Negotiate a price cap or require that any forced buy‑out be approved by a supermajority of the other shareholders, and define a clear valuation methodology with a floor price."
  }
]
