{"clauses": [
  {"type": "payment", "location": "Section 1", "text": "The total tuition for the Course is $2,500. Payment is due in full before the first session. A 5% discount applies if paid within 10 days of signing."},
  {"type": "refund", "location": "Section 2", "text": "Students may request a full refund within 14 days of the start date, provided no sessions have been attended. After the 14‑day period, refunds are prorated based on sessions attended."},
  {"type": "conduct", "location": "Section 3", "text": "Students must behave respectfully, attend all live sessions, and not share course materials with non‑enrolled persons. Violations may result in suspension or termination of enrollment."},
  {"type": "arbitration", "location": "Section 4", "text": "Any dispute arising out of this Agreement shall be resolved by binding arbitration in accordance with the rules of the American Arbitration Association."},
  {"type": "confidentiality", "location": "Section 5", "text": "..."}
]}

{"risk_flags": [
  {
    "clause_type": "payment",
    "risk": "Up‑front full payment required",
    "explanation": "The student must pay the entire tuition before any services are rendered, which may be risky if the course is cancelled.",
    "mitigation": "Ask for a payment schedule or escrow arrangement."
  },
  {
    "clause_type": "arbitration",
    "risk": "Mandatory binding arbitration",
    "explanation": "Disputes cannot be taken to court, limiting legal recourse and possibly favoring the provider.",
    "mitigation": "Negotiate for a mutual arbitration clause or the option to choose mediation first."
  }
]}
