messages.content:
{
  "title": "W‑4 Form",
  "type": "object",
  "properties": {
    "filing_status": {
      "type": "string",
      "enum": ["Single", "Married filing jointly", "Married filing separately", "Head of household"],
      "description": "Select your filing status."
    },
    "dependents": {
      "type": "integer",
      "minimum": 0,
      "description": "Number of qualifying dependents."
    },
    "extra_withholding": {
      "type": "number",
      "minimum": 0,
      "description": "Additional amount to withhold each paycheck."
    }
  },
  "required": ["filing_status"]
}
