messages.content:
{
  "title": "Direct Deposit Form",
  "type": "object",
  "properties": {
    "bank_name": {"type": "string", "description": "Name of your bank"},
    "account_number": {"type": "string", "description": "Your bank account number"},
    "routing_number": {"type": "string", "description": "Bank routing number"},
    "account_type": {"type": "string", "enum": ["Checking", "Savings"], "description": "Type of account"}
  },
  "required": ["bank_name", "account_number", "routing_number", "account_type"]
}
