messages.content:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Parking Permit Request Form",
  "type": "object",
  "properties": {
    "employee_name": {"type": "string", "title": "Employee Name"},
    "employee_id": {"type": "string", "title": "Employee ID"},
    "department": {"type": "string", "title": "Department"},
    "car_make_model": {"type": "string", "title": "Car Make & Model"},
    "license_plate": {"type": "string", "title": "License Plate Number"},
    "parking_location_preference": {"type": "string", "enum": ["Lot A", "Lot B", "Lot C"], "title": "Preferred Parking Lot"},
    "start_date": {"type": "string", "format": "date", "title": "Desired Permit Start Date"}
  },
  "required": ["employee_name", "employee_id", "department", "car_make_model", "license_plate", "parking_location_preference", "start_date"]
}
