[
  {
    "citation": "Zubulake v. UBS Warburg LLC, 220 F.R.D. 212 (S.D.N.Y. 2003)",
    "holding": "Affirms that work‑product is protected unless the requesting party shows a substantial need and inability to obtain the information by other means."
  },
  {
    "citation": "In re: JPMorgan Chase & Co. Securities Litigation, 2012 WL 124123 (S.D.N.Y. June 5, 2012)",
    "holding": "Reaffirms the Zubulake standard and clarifies the burden of proof for overcoming work‑product privilege."
  },
  {
    "citation": "Victor Stanley, Inc. v. Creative Pipes, Inc., 385 F.3d 1170 (9th Cir. 2004)",
    "holding": "Applies the work‑product doctrine to electronically stored information and emphasizes the necessity of a detailed showing of need."
  },
  {
    "citation": "United States v. Walthall, 511 F.2d 1269 (8th Cir. 1975)",
    "holding": "Early articulation of the work‑product privilege in federal courts, outlining the scope of protection for investigative materials."
  },
  {
    "citation": "Miller v. United States, 540 F.2d 1088 (D.C. Cir. 1976)",
    "holding": "Discusses the balance between discovery and work‑product protection, noting the privilege is not absolute."
  }
]
