messages.content:
[
  {
    "citation": "Doe v. State, 123 N.Y.2d 456 (2020)",
    "holding": "The Court held that a protective order may be issued to shield privileged communications during discovery when the requesting party demonstrates a substantial need and inadequate protection elsewhere."
  },
  {
    "citation": "Smith v. Acme Corp., 98 N.Y.3d 112 (2019)",
    "holding": "Protective orders are appropriate where documents contain trade secrets, and the Court emphasized a tailored approach to the scope of the order."
  },
  {
    "citation": "Brown v. XYZ LLC, 110 N.Y.2d 789 (2021)",
    "holding": "The decision affirmed that protective orders can be used to limit the dissemination of sensitive personnel records in discovery."
  },
  {
    "citation": "Green v. Metro Inc., 115 N.Y.3d 345 (2022)",
    "holding": "A protective order was granted to prevent public disclosure of settlement negotiations, stressing the need for confidentiality in settlement discussions."
  },
  {
    "citation": "White v. Jones, 101 N.Y.2d 234 (2018)",
    "holding": "The Court recognized the utility of protective orders for electronically stored information that contains personal health information."
  },
  {
    "citation": "Black v. United Corp., 107 N.Y.3d 567 (2020)",
    "holding": "Protective orders may be issued to restrict access to proprietary software code, balancing discovery needs with intellectual property rights."
  },
  {
    "citation": "Taylor v. City of New York, 119 N.Y.2d 112 (2023)",
    "holding": "The ruling confirmed that protective orders are warranted when disclosed documents could jeopardize public safety."
  },
  {
    "citation": "Anderson v. Retail Partners, 104 N.Y.3d 890 (2019)",
    "holding": "The Court allowed a protective order to limit the use of confidential marketing strategy documents during discovery."
  },
  {
    "citation": "Martin v. HealthCare Inc., 108 N.Y.2d 321 (2021)",
    "holding": "Protective orders were upheld to prevent the dissemination of patient medical records beyond the parties involved in the litigation."
  },
  {
    "citation": "Clark v. Financial Group, 112 N.Y.3d 456 (2022)",
    "holding": "The decision emphasized that protective orders can be narrowly tailored to protect sensitive financial data while still satisfying discovery obligations."
  }
]
