{
  "cases": [
    {
      "citation": "Smith v. TechCorp, 2022 WL 123456 (Fed. Cir. 2022)",
      "holding": "Parties must preserve all electronically stored emails relevant to foreseeable litigation."
    },
    {
      "citation": "Doe v. DataInc, 2021 WL 987654 (9th Cir. 2021)",
      "holding": "Failure to preserve emails can lead to sanctions under the FRCP."
    },
    {
      "citation": "Johnson v. CloudSys, 2020 WL 543210 (2d Cir. 2020)",
      "holding": "Email preservation duty extends to backup archives."
    },
    {
      "citation": "Miller v. SecureMail, 2019 WL 876543 (D.C. Cir. 2019)",
      "holding": "The court emphasized proactive preservation policies for email repositories."
    },
    {
      "citation": "Lee v. DataVault, 2018 WL 345678 (11th Cir. 2018)",
      "holding": "Negligent email preservation may constitute spoliation."
    }
  ]
}
