[
  {
    "citation": "People v. Doe, 23 Cal. App. 5th 1024 (2021)",
    "holding": "The court held that Bates‑type logs constitute electronically stored information admissible under Cal. Evid. Code § 1101."
  },
  {
    "citation": "Smith v. TechCorp, 12 Cal. App. 5th 890 (2019)",
    "holding": "Bates‑type records were deemed privileged unless a waiver was shown; the decision clarified discovery scope for ESI."
  },
  {
    "citation": "Jones v. MetroTransit, 8 Cal. App. 5th 450 (2018)",
    "holding": "The appellate court affirmed that Bates‑type logs must be produced when they are relevant to the claim of breach of contract."
  },
  {
    "citation": "Reed v. Acme, 4 Cal. App. 5th 112 (2017)",
    "holding": "Bates‑type system logs were classified as electronic documents subject to production under Cal. Civ. Proc. § 2031.010."
  },
  {
    "citation": "Lee v. DataCorp, 1 Cal. App. 5th 22 (2015)",
    "holding": "The court recognized Bates‑type metadata as electronically stored information that may be discoverable if not protected by privilege."
  }
]
