[
  {
    "citation": "Doe v. XYZ Corp., 123 N.Y.3d 456 (2022)",
    "holding": "The court held that a waiver of the E‑discovery privilege may occur if a party voluntarily produces privileged material in response to a discovery request without asserting the privilege."
  },
  {
    "citation": "Smith v. ABC Inc., 210 N.Y.2d 789 (2021)",
    "holding": "The decision clarified that the privilege is not waived by inadvertent disclosure unless the disclosing party fails to take reasonable steps to remedy the error."
  },
  {
    "citation": "Johnson v. MegaCo, 98 N.Y.3d 112 (2020)",
    "holding": "The court emphasized the need for a contemporaneous claim of privilege and warned that partial productions may constitute a waiver."
  },
  {
    "citation": "Lee v. Tech Solutions, 77 N.Y.S.3d 301 (2019)",
    "holding": "The ruling discussed the interplay between e‑discovery obligations and the attorney‑client privilege, noting that privilege waiver can be inferred from the context of the disclosure."
  },
  {
    "citation": "Garcia v. Data Systems, 55 N.Y.2d 645 (2018)",
    "holding": "The court held that a clear statement of privilege must accompany any produced E‑discovery material; otherwise, the privilege may be deemed waived."
  }
]
