[
  {
    "title": "EU Introduces New Customs Documentation Requirements for US Imports",
    "url": "https://www.reuters.com/eu-new-customs-docs-us-imports-2024-09-15",
    "publication_date": "2024-09-15",
    "snippet": "Effective from October 1, 2024, the European Commission will require additional electronic certificates for certain commodity categories imported into the United States, potentially adding 2‑3 days to clearance times."
  },
  {
    "title": "European Parliament Passes Green Customs Tariff",
    "url": "https://www.ft.com/content/eu-green-customs-tariff-2024",
    "publication_date": "2024-09-12",
    "snippet": "A new tariff aimed at reducing carbon emissions will apply to goods shipped from the EU to non‑EU markets, including the US, with higher customs fees and stricter documentation."
  },
  {
    "title": "Customs Delays Forecasted as EU Rolls Out Digital Customs Platform",
    "url": "https://www.bloomberg.com/news/articles/eu-digital-customs-platform-delays",
    "publication_date": "2024-09-10",
    "snippet": "The EU's new digital customs platform, scheduled for full launch in November 2024, is expected to cause temporary processing bottlenecks as carriers adapt to the system."
  },
  {
    "title": "US Trade Agency Warns of Potential EU Regulation Impact",
    "url": "https://www.cnn.com/2024/09/08/us-trade-agency-eu-regulation-warning",
    "publication_date": "2024-09-08",
    "snippet": "The US International Trade Administration issued an alert noting that recent EU customs rule changes may increase clearance times for high‑value electronics."
  },
  {
    "title": "Logistics Firm Aena Reports Longer Port Dwell Times in Rotterdam",
    "url": "https://www.logisticsnews.com/eu-port-dwell-times-2024",
    "publication_date": "2024-09-14",
    "snippet": "After the EU introduced new customs paperwork, Aena observed an average increase of 1.5 days in container dwell time at the Port of Rotterdam."
  }
]
