[
  {
    "title": "South Korean factory workers stage nationwide strike over wages",
    "url": "https://news.example.com/south-korea-factory-strike",
    "publication_date": "2025-12-05",
    "snippet": "Over 10,000 workers at major semiconductor fabs in Hwaseong and Cheongju walked off the job, demanding higher wages and better overtime pay. The strike began Tuesday and may continue for several days."
  },
  {
    "title": "Semiconductor giant Samsung warns of production delays due to labor unrest",
    "url": "https://news.example.com/samsung-production-delay",
    "publication_date": "2025-12-04",
    "snippet": "Samsung Electronics announced that ongoing labor protests at its wafer fabs could delay shipments of memory chips to customers worldwide."
  },
  {
    "title": "South Korean government considers intervention in semiconductor labor dispute",
    "url": "https://news.example.com/government-intervention",
    "publication_date": "2025-12-05",
    "snippet": "The Ministry of Trade, Industry and Energy is in talks with union leaders to mediate the strike, citing concerns over global supply‑chain impacts."
  }
]
