  {
    "title": "Rare‑Earth Prices Surge Amid Chinese Export Curbs",
    "url": "https://www.reuters.com/business/rare-earth-prices-surge-china-2025-11-28/",
    "publication_date": "2025-11-28",
    "snippet": "China’s tighter export controls have pushed rare‑earth ore prices up 22% in the last month, sparking concerns for electronics manufacturers worldwide."
  },
  {
    "title": "U.S. Auto Makers Brace for Higher EV Battery Costs",
    "url": "https://www.bloomberg.com/news/articles/2025-11-27/auto-makers-rare-earth-costs",
    "publication_date": "2025-11-27",
    "snippet": "The recent spike in neodymium and dysprosium prices is expected to add $300‑$500 per battery pack, according to industry analysts."
  },
  {
    "title": "Rare‑Earth Market Volatility Hits Record Levels",
    "url": "https://www.ft.com/content/rare-earth-market-volatility-2025",
    "publication_date": "2025-11-26",
    "snippet": "A combination of geopolitical tension in the South China Sea and supply bottlenecks in Mongolia has driven rare‑earth index volatility to its highest level in a decade."
  },
  {
    "title": "Tech Giants Seek Alternative Materials as Rare‑Earth Costs Rise",
    "url": "https://www.wsj.com/articles/tech-alternatives-rare-earth-2025-11-25",
    "publication_date": "2025-11-25",
    "snippet": "Major semiconductor firms are accelerating R&D on substitute magnet materials after a 18% week‑over‑week price jump in praseodymium."
  },
  {
    "title": "Mining Protest in Brazil Threatens Rare‑Earth Supply Chain",
    "url": "https://www.theguardian.com/environment/2025/nov/24/brazil-rare-earth-protest",
    "publication_date": "2025-11-24",
    "snippet": "Local communities have halted operations at a key rare‑earth mine, adding another layer of risk to the already tight market."
  }
]
