[
  {
    "title": "Recent Cyber‑Attack Exploits Open Ports",
    "url": "https://securitynews.example.com/article123",
    "publication_date": "2025-12-08",
    "snippet": "Attackers leveraged misconfigured services on ports 22, 80, 443, 3306, and 8080 to gain unauthorized access."
  },
  {
    "title": "Top 10 Ports Frequently Targeted in 2025",
    "url": "https://cyberintel.example.com/top10ports",
    "publication_date": "2025-12-07",
    "snippet": "The most exploited ports this year were 21 (FTP), 23 (Telnet), 25 (SMTP), 110 (POP3), and 3389 (RDP)."
  },
  {
    "title": "Security Advisory: Unpatched Services on Common Ports",
    "url": "https://vendoradvisory.example.com/2025/12",
    "publication_date": "2025-12-06",
    "snippet": "Several vendors reported vulnerabilities on ports 53 (DNS), 143 (IMAP), and 5900 (VNC) that could be used for hijacking if not patched."
  }
]
