{
  "concepts": [
    {"concept": "Respect and Dignity", "description": "Treat everyone with respect; no harassment, discrimination, or bullying."},
    {"concept": "Integrity and Honesty", "description": "Act honestly, avoid conflicts of interest, and report wrongdoing."},
    {"concept": "Confidentiality", "description": "Safeguard confidential company information and personal data."},
    {"concept": "Compliance with Laws", "description": "Follow all applicable laws, including anti‑bribery and trade rules."},
    {"concept": "Use of Company Assets", "description": "Use resources responsibly; personal use must be limited and not affect work."}
  ]
}

{
  "quiz_id": "quiz_fde2024",
  "questions": [
    {"question": "Which behavior is prohibited under the Respect and Dignity principle?", "options": ["Harassment", "Professional collaboration", "Constructive feedback", "Mentoring"], "answer": "Harassment"},
    {"question": "What should an employee do if they discover a potential conflict of interest?", "options": ["Ignore it", "Report it to management", "Hide the information", "Seek personal gain"], "answer": "Report it to management"},
    {"question": "Confidentiality primarily protects what type of information?", "options": ["Public press releases", "Company trade secrets and personal data", "Office décor choices", "Holiday schedules"], "answer": "Company trade secrets and personal data"},
    {"question": "Which of the following is an example of complying with laws?", "options": ["Offering a bribe to a vendor", "Ignoring anti‑bribery regulations", "Following anti‑bribery policies", "Sharing confidential client data publicly"], "answer": "Following anti‑bribery policies"},
    {"question": "Appropriate use of company assets means:", "options": ["Using a laptop for personal gaming all day", "Reasonable personal use that does not interfere with work", "Selling company equipment for profit", "Copying proprietary code to a personal device"], "answer": "Reasonable personal use that does not interfere with work"}
  ]
}
