{"concepts": [
  {"concept": "Net‑zero commitment", "description": "Achieve net‑zero carbon emissions by 2040."},
  {"concept": "Scope‑1 & Scope‑2 reduction", "description": "Cut emissions 50% by 2030 (baseline 2020)."},
  {"concept": "Renewable electricity", "description": "60% of electricity from renewables by 2028."},
  {"concept": "Carbon‑offset program", "description": "Offset 20% of remaining emissions by 2035."},
  {"concept": "Annual reporting", "description": "Submit carbon‑footprint report by 31 March each year."},
  {"concept": "Governance", "description": "Quarterly review by Sustainability Committee."}
]}

{"quiz_id": "quiz_42c8f7", "questions": [
  {"question": "By which year must the company achieve net‑zero emissions?", "options": ["2035","2040","2050","2045"], "answer": "2040"},
  {"question": "What is the targeted reduction for Scope‑1 and Scope‑2 emissions by 2030?", "options": ["30%","40%","50%","60%"], "answer": "50%"},
  {"question": "Which percentage of electricity should come from renewable sources by 2028?", "options": ["40%","50%","60%","70%"], "answer": "60%"},
  {"question": "What portion of remaining emissions will be offset by 2035?", "options": ["10%","20%","30%","40%"], "answer": "20%"},
  {"question": "When is the annual carbon‑footprint report due each year?", "options": ["31 January","31 March","30 June","31 December"], "answer": "31 March"}
]}
