{
  "quiz_id": "quiz_abc123",
  "questions": [
    {
      "question": "Which benefit provides medical, dental, and vision coverage?",
      "options": ["Wellness Programs", "Health Insurance", "Employee Assistance", "Flexible Working"],
      "answer": "Health Insurance"
    },
    {
      "question": "What is the company’s matching contribution for the 401(k) plan?",
      "options": ["3%", "5%", "7%", "10%"],
      "answer": "5%"
    },
    {
      "question": "How many total paid days off (vacation, sick, personal) does a new hire receive per year?",
      "options": ["30", "35", "40", "45"],
      "answer": "35"
    },
    {
      "question": "Which program includes gym membership reimbursement?",
      "options": ["Wellness Programs", "Health Insurance", "Employee Assistance", "Flexible Working"],
      "answer": "Wellness Programs"
    },
    {
      "question": "What flexible‑working options are offered?",
      "options": ["Remote work and flexible hours", "Compressed workweek only", "Job‑sharing", "No flexibility"],
      "answer": "Remote work and flexible hours"
    },
    {
      "question": "Where can employees obtain confidential counseling?",
      "options": ["Employee Assistance", "Wellness Programs", "Health Insurance", "Retirement Plan"],
      "answer": "Employee Assistance"
    }
  ]
}
