{
  "quiz_id": "quiz_2023_aes",
  "questions": [
    {
      "question": "What key length is required for encrypting regulated data such as PII?",
      "options": ["128‑bit", "192‑bit", "256‑bit", "Any length"],
      "answer": "256‑bit"
    },
    {
      "question": "Which AES mode is recommended for authenticated encryption?",
      "options": ["CBC", "ECB", "GCM", "CFB"],
      "answer": "GCM"
    },
    {
      "question": "How often must 256‑bit keys be rotated according to the 2023 guidance?",
      "options": ["Every 5 years", "Every 2 years", "Annually", "Never"],
      "answer": "Annually"
    },
    {
      "question": "When using GCM, what is the recommended IV size?",
      "options": ["64‑bit", "96‑bit", "128‑bit", "Random size"],
      "answer": "96‑bit"
    },
    {
      "question": "Which of the following is NOT part of the compliance checklist?",
      "options": ["Verify key length", "Ensure GCM 96‑bit IV", "Document rotation schedule", "Use ECB mode"],
      "answer": "Use ECB mode"
    }
  ]
}
