{
  "concepts": [
    {
      "concept": "Hazard Communication Program",
      "description": "Written program that describes how chemical hazards are evaluated and communicated to employees."
    },
    {
      "concept": "Hazard Classification",
      "description": "Process for categorizing chemicals based on physical, health, and environmental hazards."
    },
    {
      "concept": "Label Requirements",
      "description": "Labels must include product identifier, signal word, hazard statements, precautionary statements, and supplier ID."
    },
    {
      "concept": "Safety Data Sheets (SDS)",
      "description": "SDS must be available for each hazardous chemical and contain 16 standardized sections."
    },
    {
      "concept": "Employee Training",
      "description": "Training must cover chemical hazards, label reading, SDS usage, and protective measures."
    }
  ]
}

{
  "quiz_id": "quiz_001",
  "questions": [
    {
      "question": "What is the primary purpose of a Hazard Communication Program?",
      "options": ["To document incident reports", "To evaluate and communicate chemical hazards", "To schedule equipment maintenance", "To certify employee competence"],
      "answer": "To evaluate and communicate chemical hazards"
    },
    {
      "question": "According to OSHA 1910.1200, which of the following must be included on a chemical label?",
      "options": ["Expiration date", "Signal word", "Employee ID", "Company revenue"],
      "answer": "Signal word"
    },
    {
      "question": "How many sections are required in a Safety Data Sheet (SDS)?",
      "options": ["10", "12", "14", "16"],
      "answer": "16"
    },
    {
      "question": "Which concept describes the process of grouping chemicals based on their properties?",
      "options": ["Labeling", "Hazard Classification", "PPE selection", "Ventilation design"],
      "answer": "Hazard Classification"
    },
    {
      "question": "Employee training under OSHA 1910.1200 must cover all EXCEPT:",
      "options": ["Proper use of SDS", "Reading chemical labels", "How to write a hazard communication program", "Protective measures"],
      "answer": "How to write a hazard communication program"
    }
  ]
}
