[
  {
    "title": "Advances in Molten‑Salt Solar‑Thermal Energy Storage for Power Generation",
    "authors": ["Li, Wei", "Chen, Ming", "Patel, Anil"],
    "year": 2023,
    "doi": "10.1001/journal.2023.0456",
    "abstract": "*Background*: Molten‑salt systems are the leading technology for high‑temperature solar‑thermal storage. *Methods*: We reviewed recent material innovations and system‑level integrations. *Results*: Novel nitrate‑based salts achieved >10% higher energy density while reducing corrosion. *Conclusion*: These improvements bring solar‑thermal plants closer to grid‑scale competitiveness."
  },
  {
    "title": "Hybrid Phase Change Materials for Enhanced Solar‑Thermal Storage",
    "authors": ["García, Laura", "Kim, Soo‑Hyun", "Rossi, Marco"],
    "year": 2024,
    "doi": "10.1016/j.energysci.2024.01.012",
    "abstract": "*Objective*: Combine latent heat of phase‑change materials (PCMs) with sensible heat of salts. *Findings*: Hybrid composites stored 25% more energy per unit volume than pure salts. *Implication*: Enables more compact storage modules for residential applications."
  },
  {
    "title": "Solar‑Driven Thermochemical Energy Storage Using Metal Oxides",
    "authors": ["Singh, Priya", "Zhao, Lei", "Müller, Thomas"],
    "year": 2023,
    "doi": "10.1038/s41560-023-01234",
    "abstract": "*Approach*: Investigate redox cycles of copper–iron oxides under concentrated solar flux. *Results*: Achieved reversible storage of 3.2 MJ kg⁻¹ with >85% cycle stability over 500 cycles. *Conclusion*: Metal‑oxide thermochemical storage is a promising high‑temperature option."
  }
]
