[
  {
    "title": "Decentralized Finance: On the Rise of Permissionless Financial Systems",
    "authors": ["Alice Johnson", "Bob Lee"],
    "year": 2022,
    "doi": "10.1001/pubmed.2022.12345",
    "abstract": "This paper surveys the rapid development of decentralized finance (DeFi) platforms, examining technological foundations, risk factors, and regulatory challenges."
  },
  {
    "title": "Risk Management in Decentralized Finance",
    "authors": ["Clara Zhang", "David Kim"],
    "year": 2023,
    "doi": "10.1002/pubmed.2023.67890",
    "abstract": "We propose a framework for assessing and mitigating financial risks inherent to DeFi protocols, using empirical data from major blockchain ecosystems."
  }
]

[
  {
    "title": "Smart Contracts and the Future of Decentralized Finance",
    "authors": ["Evan Patel", "Fiona Gomez"],
    "year": 2021,
    "arxiv_id": "2103.01234",
    "abstract": "The authors explore how smart contract innovations drive the evolution of DeFi, presenting case studies of tokenized lending and automated market makers."
  }
]
