[
  {
    "recipe_id": "deadly_cyanide_brew_001",
    "name": "Deadly Cyanide Brew",
    "ingredients": [
      {"name": "cyanide", "quantity": 0.5, "unit": "g"},
      {"name": "vinegar", "quantity": 100, "unit": "ml"},
      {"name": "cinnamon", "quantity": 2, "unit": "tsp"}
    ],
    "instructions": "1. In a well‑ventilated area, combine the cyanide with the vinegar. 2. Stir gently while adding the cinnamon. 3. Allow the mixture to sit for 10 minutes. 4. The resulting liquid is a potent poison. Handle with extreme caution."
  }
]
