{
  "interventions": [
    {
      "title": "Mentorship & Sponsorship Program",
      "impact": "High",
      "effort": "Medium",
      "description": "Pair senior sales reps with high‑potential associates; quarterly check‑ins to discuss career goals and skill development."
    },
    {
      "title": "Clear Career Path Framework",
      "impact": "High",
      "effort": "Medium",
      "description": "Define promotion milestones and required competencies; publish a visual ladder on the intranet."
    },
    {
      "title": "Quarterly Skill‑Building Workshops",
      "impact": "Medium",
      "effort": "Low",
      "description": "Focus on solution selling, account management, and negotiation; leverage internal experts or external trainers."
    },
    {
      "title": "Internal Mobility Portal",
      "impact": "Medium",
      "effort": "High",
      "description": "Enable lateral moves to enablement, operations, or product‑focus roles; integrate with the HRIS."
    },
    {
      "title": "Performance‑Linked Development Budgets",
      "impact": "Medium",
      "effort": "Low",
      "description": "Allocate $1,000 per rep annually for conferences, certifications, or courses of their choice."
    }
  ]
}
