messages.content:
[
  {
    "intervention": "Launch a 6‑month Engineering Mentorship Program",
    "description": "Pair junior engineers with senior staff for bi‑weekly one‑on‑ones, focusing on skill development and career path planning.",
    "expected_impact": "Reduce career‑growth related turnover by ~20%",
    "effort": "Medium (requires mentor recruitment and scheduling platform)"
  },
  {
    "intervention": "Introduce Transparent Promotion Ladders",
    "description": "Publish clear criteria and timelines for moving from IC1 to IC3, with quarterly review cycles.",
    "expected_impact": "Increase perceived growth opportunities by 15‑30%",
    "effort": "Low‑Medium (documentation and communication)"
  },
  {
    "intervention": "Quarterly Technical Upskilling Workshops",
    "description": "Offer hands‑on courses on emerging tech (e.g., cloud architecture, AI/ML) tied to internal certification badges.",
    "expected_impact": "Boost engagement scores and retention among <2‑year engineers",
    "effort": "Medium (external trainers or internal SMEs)"
  }
]
