{
  "risk_factor": "career_growth",
  "employee_id": "E12345",
  "interventions": [
    {
      "title": "Structured Design Leadership Path",
      "description": "Create a 12‑month roadmap that moves Maya from senior designer to lead designer, including quarterly stretch goals, mentorship from the Head of Design, and a formal title change.",
      "expected_impact": "High",
      "effort": "Medium"
    },
    {
      "title": "Cross‑functional Rotation",
      "description": "Assign Maya to a 3‑month rotation with the Product Management team to lead UX research for a flagship feature, expanding her strategic experience.",
      "expected_impact": "Medium",
      "effort": "Low"
    },
    {
      "title": "Advanced Skills Sponsorship",
      "description": "Fund Maya’s enrollment in a recognized Design Leadership certification (e.g., Nielsen Norman Group), and schedule a knowledge‑share session after completion.",
      "expected_impact": "Medium",
      "effort": "Low"
    }
  ]
}
