{
  "entries": [
    {
      "title": "Amendment to EU Data Retention Directive – Article 5",
      "link": "https://eur-lex.europa.eu/legal-content/EN/TXT/?uri=CELEX:32023L1234",
      "published": "2025-12-03",
      "summary": "Effective 2026‑01‑01, the amendment shortens the mandatory retention period for personal data from 5 years to 3 years for non‑essential data."
    },
    {
      "title": "New Guidance on Cloud Data Retention",
      "link": "https://eur-lex.europa.eu/legal-content/EN/TXT/?uri=CELEX:32023L5678",
      "published": "2025-12-05",
      "summary": "Clarifies that cloud‑service providers must retain logs for a minimum of 2 years and make them accessible to regulators."
    }
  ]
}

{
  "entries": [
    {
      "title": "FTC Final Rule on Data Retention for Financial Services",
      "link": "https://www.ftc.gov/system/files/documents/2025/12/ftc_rule_2025_04.pdf",
      "published": "2025-12-04",
      "summary": "Institutes a 4‑year retention requirement for transaction records and mandates secure disposal after the period expires."
    },
    {
      "title": "Proposed Rule: Retention of Consumer Health Data",
      "link": "https://www.ftc.gov/system/files/documents/2025/12/ftc_proposed_2025_07.pdf",
      "published": "2025-12-06",
      "summary": "Would require a minimum 5‑year retention for health‑related consumer data, with stricter access controls."
    }
  ]
}

{
  "clauses": [
    {"id": "Art5-1", "text": "Personal data not essential for the purpose of processing shall be deleted after three (3) years from the date of collection."},
    {"id": "Art5-2", "text": "Member States may extend the period to five (5) years for data required for scientific research, subject to strict safeguards."}
  ]
}

{
  "clauses": [
    {"id": "Guid-2", "text": "Cloud‑service providers must retain access logs for a minimum of two (2) years and provide them to regulators on request."},
    {"id": "Guid-3", "text": "Log retention periods shall be reviewed annually and adjusted if necessary."}
  ]
}

{
  "clauses": [
    {"id": "Sec4-1", "text": "Financial institutions shall retain all transaction records for four (4) years from the date of the transaction."},
    {"id": "Sec4-2", "text": "Records must be disposed of securely after the retention period expires."}
  ]
}

{
  "clauses": [
    {"id": "Prop5-1", "text": "Consumer health data must be retained for a minimum of five (5) years after collection."},
    {"id": "Prop5-2", "text": "During retention, data must be encrypted and access limited to authorized personnel only."}
  ]
}

{
  "matches": [
    {"control_id": "CTRL-DR-001", "clause_id": "Art5-1", "relevance": 0.98},
    {"control_id": "CTRL-DR-003", "clause_id": "Prop5-2", "relevance": 0.95}
  ],
  "gaps": [
    {"control_id": "CTRL-DR-002", "missing_clause": "Retention period is 4 years (US) vs 5 years internal"},
    {"control_id": "CTRL-DR-004", "missing_clause": "EU requires 2‑year cloud log retention (Guid‑2)"}
  ]
}
