{
  "median_salary": 95000,
  "growth_rate": "12% annual growth",
  "key_skills": ["user research", "wireframing", "prototyping", "Figma", "Sketch", "Usability testing", "Interaction design", "Information architecture"]
}

{
  "timeline": [
    {
      "month_range": "1‑2",
      "focus": "Foundations",
      "actions": [
        "Complete ‘Google UX Design Professional Certificate’ (Coursera) – 4 weeks",
        "Finish ‘Figma for Beginners’ (DesignLab) – 2 weeks",
        "Read ‘Don’t Make Me Think’ by Steve Krug"
      ],
      "deliverable": "Certificate of completion and Figma practice file"
    },
    {
      "month_range": "3‑4",
      "focus": "Practical Skills",
      "actions": [
        "Enroll in ‘UX Research Methods’ (Interaction Design Foundation) – 3 weeks",
        "Build 2 low‑fidelity case studies: (a) Redesign a landing page, (b) Improve a mobile onboarding flow",
        "Share work on Dribbble and receive feedback"
      ],
      "deliverable": "Two case studies in portfolio (PDF + live prototype)"
    },
    {
      "month_range": "5‑6",
      "focus": "Intermediate Projects",
      "actions": [
        "Join a local UX meetup group (Austin UX Users) and attend 2 events",
        "Volunteer for a nonprofit to redesign their website (real‑world experience)",
        "Complete ‘Prototyping with Sketch & InVision’ workshop – 2 weeks"
      ],
      "deliverable": "Full‑scale redesign project added to portfolio"
    },
    {
      "month_range": "7‑9",
      "focus": "Advanced Portfolio & Job Prep",
      "actions": [
        "Take the ‘Certified UX Designer (Nielsen Norman Group)’ exam preparation course",
        "Refine all case studies using feedback, focus on storytelling",
        "Create a tailored resume and LinkedIn profile with UX keywords",
        "Apply to 5 entry‑level UX roles per week"
      ],
      "deliverable": "Polished 4‑case portfolio, certified resume, and active job applications"
    },
    {
      "month_range": "10‑12",
      "focus": "Interview Mastery & Offer Negotiation",
      "actions": [
        "Schedule mock interviews with a CareerNavigator coach (2 sessions)",
        "Practice whiteboard design challenges weekly",
        "Negotiate offers using market salary data"
      ],
      "deliverable": "Job offer(s) and transition plan to first UX role"
    }
  ]
}
