{
  "timeline_months": 12,
  "steps": [
    {
      "month_range": "1‑2",
      "goal": "Fill skill gaps",
      "actions": [
        "Complete an online Figma course (e.g., Coursera UI/UX Design Specialization).",
        "Practice wireframing by redesigning two existing web pages."
      ],
      "resources": ["Figma Beginners Guide", "UX Design on Coursera"]
    },
    {
      "month_range": "3‑4",
      "goal": "Build portfolio pieces",
      "actions": [
        "Create 2 case studies showing end‑to‑end product design process (research, ideation, prototype).",
        "Document process on Behance with focus on user research and interaction design."
      ],
      "resources": ["Behance Portfolio Tips"]
    },
    {
      "month_range": "5‑6",
      "goal": "Earn certification",
      "actions": ["Obtain the Certified UX Designer credential from Nielsen Norman Group (NN/g)."],
      "resources": ["Nielsen Norman Group UX Certification"]
    },
    {
      "month_range": "7‑9",
      "goal": "Gain practical experience",
      "actions": [
        "Apply for junior product designer contract roles or internships at Bay Area startups.",
        "Volunteer to redesign a product feature for a non‑profit organization."
      ],
      "resources": ["AngelList Jobs", "VolunteerMatch"]
    },
    {
      "month_range": "10‑12",
      "goal": "Job application and interview prep",
      "actions": [
        "Tailor resume and LinkedIn profile to product‑design keywords.",
        "Practice behavioral and design interview questions using mock interviews."
      ],
      "resources": ["Cracking the PM Interview", "Design Sprint Workbook"]
    }
  ],
  "target_keywords": ["Product Design", "User Research", "Wireframing", "Figma", "Prototyping", "Interaction Design"]
}
