// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title ExecutiveEmploymentContract
 * @dev A smart‑contract representation of an executive employment agreement for TechNova Inc.
 */
contract ExecutiveEmploymentContract {
    address public employer = 0x1234567890abcdef1234567890abcdef12345678; // TechNova Inc.
    address public executive = 0xabcdefabcdefabcdefabcdefabcdefabcdefabcd; // Jane Doe
    uint256 public startDate = 1735689600; // 2025‑01‑01 (unix timestamp)
    uint256 public termInSeconds = 3 * 365 days;
    uint256 public salary = 250_000 ether; // expressed in wei for demonstration
    uint256 public bonusPct = 20; // percent
    uint256 public rsuAmount = 5000;

    // ---------- Mandatory CA clauses ----------
    string public constant NON_COMPETE_VOIDE = "Under California Business and Professions Code §16600, any post‑employment non‑compete is void.";
    string public nonSolicitationClause = "Executive agrees not to solicit TechNova's customers or employees for a period of 12 months within the California Bay Area after termination.";
    string public confidentialityClause = "Executive shall keep all confidential, trade‑secret, and proprietary information of TechNova confidential both during and after employment.";

    // State variables for termination
    bool public terminated = false;
    uint256 public terminationDate;

    // Events
    event Terminated(uint256 date);
    event SalaryPaid(uint256 amount, uint256 date);
    event BonusPaid(uint256 amount, uint256 date);
    event RSUGranted(uint256 amount);

    // Modifiers
    modifier onlyEmployer() {
        require(msg.sender == employer, "Only employer can call");
        _;
    }
    modifier onlyExecutive() {
        require(msg.sender == executive, "Only executive can call");
        _;
    }

    // ---------------------------------------------------
    // Functions representing contract lifecycle
    // ---------------------------------------------------
    function terminate(uint256 _date) external onlyEmployer {
        terminated = true;
        terminationDate = _date;
        emit Terminated(_date);
    }

    function paySalary() external onlyEmployer {
        // Simplified payment logic for demo
        emit SalaryPaid(salary, block.timestamp);
    }

    function payBonus(uint256 performanceScore) external onlyEmployer {
        uint256 bonus = (salary * bonusPct * performanceScore) / (100 * 100);
        emit BonusPaid(bonus, block.timestamp);
    }

    function grantRSU() external onlyEmployer {
        emit RSUGranted(rsuAmount);
    }

    // View functions for clauses
    function viewNonCompeteClause() external pure returns (string memory) {
        return NON_COMPETE_VOIDE;
    }
    function viewNonSolicitationClause() external view returns (string memory) {
        return nonSolicitationClause;
    }
    function viewConfidentialityClause() external view returns (string memory) {
        return confidentialityClause;
    }
}

