// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title InnovateX Inc. Board Multi‑Signature Wallet
 * @notice This contract implements a 2‑of‑3 multi‑signature wallet for InnovateX Inc.
 * Mandatory Delaware corporate governance clauses are enforced via modifiers and events.
 */
contract InnovateXBoardWallet {
    // ---------- Mandatory Delaware Clauses ----------
    // Quorum Requirement: at least a majority of board members must be present to form a quorum.
    uint256 constant MIN_QUORUM = 2; // Majority of 3 is 2
    // Approval Threshold: disbursements must be approved by at least two‑thirds of voting members present at a quorum.
    uint256 constant APPROVAL_THRESHOLD = 2; // Two‑thirds of 3 (rounded up) = 2
    // Fiduciary Duty Clause: directors must act in good faith and in the best interests of the corporation.
    // Record‑Keeping: detailed minutes of each transaction approval must be retained.
    // Amendment Procedure: signatory list changes require a formal board resolution and filing with the Secretary of State.
    // Limitation of Liability: corporation not liable for losses unless caused by gross negligence.

    // ---------- Wallet State ----------
    address[] public signers;
    mapping(address => bool) public isSigner;
    uint256 public transactionCount;

    struct Transaction {
        address to;
        uint256 value;
        bytes data;
        bool executed;
        uint256 confirmationCount;
        mapping(address => bool) confirmations;
    }

    mapping(uint256 => Transaction) public transactions;

    // ---------- Events ----------
    event Deposit(address indexed sender, uint256 amount);
    event Submission(uint256 indexed txId);
    event Confirmation(address indexed sender, uint256 indexed txId);
    event Execution(uint256 indexed txId);
    event Revocation(address indexed sender, uint256 indexed txId);

    // ---------- Modifiers ----------
    modifier onlySigner() {
        require(isSigner[msg.sender], "Not authorized signer");
        _;
    }

    modifier txExists(uint256 _txId) {
        require(_txId < transactionCount, "Tx does not exist");
        _;
    }

    modifier notExecuted(uint256 _txId) {
        require(!transactions[_txId].executed, "Tx already executed");
        _;
    }

    modifier notConfirmed(uint256 _txId) {
        require(!transactions[_txId].confirmations[msg.sender], "Tx already confirmed");
        _;
    }

    constructor() {
        // Initialize signers (board members)
        address alice = 0xAaAaAaAaAaAaAaAaAaAaAaAaAaAaAaAaAaAaAaAa;
        address bob   = 0xBbBbBbBbBbBbBbBbBbBbBbBbBbBbBbBbBbBbBbBb;
        address carol = 0xCcCcCcCcCcCcCcCcCcCcCcCcCcCcCcCcCcCcCcCc;
        signers = [alice, bob, carol];
        for (uint256 i = 0; i < signers.length; i++) {
            isSigner[signers[i]] = true;
        }
    }

    receive() external payable {
        emit Deposit(msg.sender, msg.value);
    }

    function submitTransaction(address _to, uint256 _value, bytes calldata _data)
        external
        onlySigner
    {
        uint256 txId = transactionCount;
        Transaction storage txn = transactions[txId];
        txn.to = _to;
        txn.value = _value;
        txn.data = _data;
        txn.executed = false;
        txn.confirmationCount = 0;
        transactionCount++;
        emit Submission(txId);
    }

    function confirmTransaction(uint256 _txId)
        external
        onlySigner
        txExists(_txId)
        notExecuted(_txId)
        notConfirmed(_txId)
    {
        Transaction storage txn = transactions[_txId];
        txn.confirmations[msg.sender] = true;
        txn.confirmationCount += 1;
        emit Confirmation(msg.sender, _txId);
    }

    function executeTransaction(uint256 _txId)
        external
        onlySigner
        txExists(_txId)
        notExecuted(_txId)
    {
        Transaction storage txn = transactions[_txId];
        require(txn.confirmationCount >= APPROVAL_THRESHOLD, "Not enough confirmations");
        txn.executed = true;
        (bool success,) = txn.to.call{value: txn.value}(txn.data);
        require(success, "Tx execution failed");
        emit Execution(_txId);
    }

    function revokeConfirmation(uint256 _txId)
        external
        onlySigner
        txExists(_txId)
        notExecuted(_txId)
    {
        Transaction storage txn = transactions[_txId];
        require(txn.confirmations[msg.sender], "Tx not confirmed");
        txn.confirmations[msg.sender] = false;
        txn.confirmationCount -= 1;
        emit Revocation(msg.sender, _txId);
    }
}

