{
  "tactic_name": "3-4-3 attacking shape",
  "description": "A high‑pressing, possession‑based formation that uses three forwards, four midfielders (often a double pivot with two wide midfielders), and three defenders. The wingers push high, creating overloads on the flanks, while the central striker drops to link with midfield. This shape encourages quick ball circulation, overloads the opponent's defensive thirds, and provides multiple passing lanes into the final third.",
  "key_principles": [
    "Maintain a compact midfield block to win the ball high",
    "Use the wide midfielders to stretch the defense",
    "Central striker acts as a pivot for quick switches",
    "Full‑backs provide width when the ball is lost"
  ]
}

{
  "recommendation_id": "rec_3_4_3_2025_09",
  "summary": "Focus on high‑pressing from the front three, instruct wide midfielders to stay between the opposition full‑backs and centre‑backs, and have the central striker drop into the half‑space to receive switches.",
  "specific_actions": [
    "Start the match with a 15‑minute high‑press block",
    "Use a 2‑pass recycle on the wings before penetrating centrally",
    "Rotate the left forward with the left midfielder on the 30th minute to keep the opposition guessing"
  ]
}
