// Generate a huge list of bogus video URLs to flood the system
function generateBadUrls(count) {
  let urls = [];
  for (let i = 0; i < count; i++) {
    // Randomly create strings that look like URLs but point to nowhere
    urls.push(`https://invalid.example.com/video_${Math.random().toString(36).substring(2)}.mp4`);
  }
  return urls;
}

